def plot(plot_data):
#==============================================================================
# title
#==============================================================================
    title=''
#==============================================================================
# figure format
# http://matplotlib.org/users/customizing.html?highlight=rcparams
#==============================================================================
    plot_format()
    mpl.rcParams['figure.subplot.right'] = 0.85
#==============================================================================
# grid set up
#==============================================================================
#plt.grid(True, which='major',linestyle='-')
#plt.grid(True, which='minor',linestyle='-')
#plt.grid(True, which='major')
#plt.grid(True, which='minor')
#==============================================================================
# print title
#==============================================================================
    plt.title(title, fontsize=16)
#==============================================================================
# x,y limite
#==============================================================================
    plt.xlim(0,180)
    plt.ylim(0,30)
#==============================================================================
# xy log scale
#==============================================================================
#    plt.xscale('log')
#    plt.yscale('log')
#==============================================================================
# xy axial equal
#==============================================================================
    ax1 = plt.gca()
    ax1.set_aspect('auto')
#==============================================================================
# plot lines
#==============================================================================
    plot_data.readFromFile()
    plot_data.lines[0]['ylabel'] = '$\Delta \sigma \Delta \\varepsilon$, $\Delta \\tau \Delta \\gamma$ [MPa]'
    plot_data.lines[1]['ylabel'] = '$\Delta \sigma \Delta \\varepsilon$, $\Delta \\tau \Delta \\gamma$ [MPa]'
    plot_data.plot([0,1])
    ax1.legend(loc=3)
    
#    ax2 = ax1.twinx()  # this is the important function
#    plot_data.plot([1])
#                
#    ax2.set_xlim(0,180)
#    ax2.set_ylim(0.0,30)
#    ax2.legend(loc=4)
#==============================================================================
# http://stackoverflow.com/questions/21920233/matplotlib-log-scale-tick-label-number-formatting
#==============================================================================
    ax1.xaxis.set_major_locator(MultipleLocator(20))
    ax1.xaxis.set_minor_locator(MultipleLocator(10))
    ax1.xaxis.set_major_formatter(ScalarFormatter())
#    ax1.yaxis.set_major_locator(MultipleLocator(10))
#    ax1.yaxis.set_minor_locator(MultipleLocator(0.005))
#    ax1.yaxis.set_major_formatter(ScalarFormatter())
